contract RateHash {
    // ========================================================
    // STORAGE
    // ========================================================

    address public drs;
    uint8 constant MIN_UINT = 0;
    uint8 constant MAX_UINT = 100;

    // ========================================================
    // EVENTS
    // ========================================================

    event NewRating(
        address _from,
        address indexed _to,
        bytes32 _txhash,
        uint8 _score
