    }

    // ========================================================
    // MAIN METHODS
    // ========================================================

    function registerNew(address _address) public {
        IDRS(drs).register(_address);
    }

    /// @notice assign a rating to another valid address
    function rate(
        address _to,
        bytes32 _txHash,
        uint8 _score
    ) external {
        if (!IDRS(drs).registered(msg.sender)) {
            revert Errors.UserNotRegistered(msg.sender);
